blockquote.programlisting:
#!/bin/sh ################################################################################ # Script to create a bridge between eth0, eth1 and eth2 # # This program is under GPL [http://www.gnu.org/copyleft/gpl.htm] # # (c) 2004 - Tom Eastep (teastep@shorewall.net) # # Modify the following variables to match your configuration # # chkconfig: 2345 05 89 # description: Layer 2 Bridge # ################################################################################
PATH=$PATH:/sbin:/usr/sbin:/usr/local/sbin
do_stop() { echo "Stopping Bridge" brctl delbr br0 ip link set eth0 down ip link set eth1 down ip link set eth2 down }
do_start() {
echo "Starting Bridge" ip link set eth0 up ip link set eth1 up ip link set eth2 up brctl addbr br0 brctl addif br0 eth0 brctl addif br0 eth1 brctl addif br0 eth2 }
case "$1" in start) do_start ;; stop) do_stop ;; restart) do_stop sleep 1 do_start ;; *) echo "Usage: $0 {start|stop|restart}" exit 1 esac exit 0
